mapper.select:
select count(user_id)
from members
where user_id = #{userId}

mapper.update:
update members
user_password = #{userPwd}, email_id = #{emailId}, email_domain = #{emailDomain}, user_name = #{userName},
where user_id = #{userId}

mapper.select:
select user_id, user_name, token
from members
where user_id = #{userId} and user_password = #{userPwd}

select user_name, user_id, email_id, email_domain, join_date
from members
where user_id = #{userId}

mapper.update:
update members
set token = #{token}
where user_id = #{userId}

mapper.select:
select token
from members
where user_id = #{userId}

mapper.update:
update members
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userId}
